import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{o as e,c as r,f as i}from"./app-bac223ee.js";const h={},n=i('<h2 id="_1-应试技巧" tabindex="-1"><a class="header-anchor" href="#_1-应试技巧" aria-hidden="true">#</a> 1. 应试技巧</h2><h3 id="_1-1-阅读顺序" tabindex="-1"><a class="header-anchor" href="#_1-1-阅读顺序" aria-hidden="true">#</a> 1.1 阅读顺序</h3><p>边读文章边做题</p><h3 id="_1-2-做题方法" tabindex="-1"><a class="header-anchor" href="#_1-2-做题方法" aria-hidden="true">#</a> 1.2 做题方法</h3><h4 id="题型1-判断题" tabindex="-1"><a class="header-anchor" href="#题型1-判断题" aria-hidden="true">#</a> 题型1: 判断题</h4><ul><li>技巧1: 文章第一种题型是判断题, 那么第一小题很有可能对应<strong>第一段</strong></li><li>技巧2: 判断题的所有小题都是<strong>顺序出题</strong></li><li>技巧3: 当文章1是<strong>判断题+填空题</strong>的组合时, 两种题型很有可能<strong>平均瓜分</strong>原文</li></ul><h4 id="题型2-填空题" tabindex="-1"><a class="header-anchor" href="#题型2-填空题" aria-hidden="true">#</a> 题型2: 填空题</h4><ul><li>技巧1: 填空题的所有小题是<strong>顺序出题</strong></li><li>技巧2: 当文章1是<strong>判断题+填空题</strong>的组合时, 两种题型很有可能<strong>平均瓜分</strong>原文</li></ul><h4 id="题型3-段落匹配-需要全文定位" tabindex="-1"><a class="header-anchor" href="#题型3-段落匹配-需要全文定位" aria-hidden="true">#</a> 题型3: 段落匹配(需要全文定位)</h4><ul><li>技巧1: 先预读所有小标题, 然后再读一遍, 加深印象</li><li>技巧2: 对小标题做联想, 预测这一段可能在讲什么内容</li><li>技巧2: 读原文, 每读1段, 回题目选择对应标题</li></ul><h4 id="题型4-多选题-需要全文定位" tabindex="-1"><a class="header-anchor" href="#题型4-多选题-需要全文定位" aria-hidden="true">#</a> 题型4: 多选题(需要全文定位)</h4><ul><li>技巧1: 预读题干 + 所有选项, 所有选项都是<strong>顺序出现</strong></li><li>技巧2: 有些题目会指定段落</li></ul><h4 id="题型5-同义选择题" tabindex="-1"><a class="header-anchor" href="#题型5-同义选择题" aria-hidden="true">#</a> 题型5: 同义选择题</h4><p>主要考察词汇量, 做法同<a href="#%E9%A2%98%E5%9E%8B2-%E5%A1%AB%E7%A9%BA%E9%A2%98">题型2</a></p><h4 id="题型6-人名匹配题" tabindex="-1"><a class="header-anchor" href="#题型6-人名匹配题" aria-hidden="true">#</a> 题型6: 人名匹配题</h4><ul><li>技巧1: 预读人名</li><li>技巧2: 读原文, 遇到第一个被考察的人名时, 回到题目*进行选择</li></ul><h4 id="题型7-单选题" tabindex="-1"><a class="header-anchor" href="#题型7-单选题" aria-hidden="true">#</a> 题型7: 单选题</h4><ul><li>技巧1: 题干会提供充足的定位信息, 因此读文章前, 预读题干就行</li><li>技巧2: 所有小题和原文是<strong>顺序对应</strong></li></ul>',18),d=[n];function l(t,s){return e(),r("div",null,d)}const u=a(h,[["render",l],["__file","IELTS_Tips_Reading.html.vue"]]);export{u as default};
